using UnityEngine;using System.Collections;public class ArrowBehaviour : MonoBehaviour {    public float SpeedInit = 10f;    public float X_start = 0.5f;    public float X_limit = 9.5f;    public float Deceleration = 5f;    private int Dice = 0;    private bool isRollingInTheDeep = false;    private bool isDecelerating = false;    private float speed = 10f;    void Update() {        if (isRollingInTheDeep) {            if (Input.GetKeyDown(KeyCode.K)) {                this.isDecelerating = true;            }            this.transform.position = new Vector3(                this.transform.position.x + speed * Time.deltaTime,                this.transform.position.y,                this.transform.position.z);            if (this.transform.position.x > X_limit) {                this.transform.position = new Vector3(                    this.X_start,                    this.transform.position.y,                    this.transform.position.z);            }            if (this.isDecelerating) {                this.speed -= this.Deceleration * Time.deltaTime;            }            if (this.speed <= 0) {                isRollingInTheDeep = false;                this.isDecelerating = false;                 this.Dice = (int)((this.transform.position.x - 0.5f) * 6 / 9) + 1;                MyGameManager.instance.ActivePlayer.Move(this.Dice);            }        }        if (Input.GetKeyDown(KeyCode.L)) {            this.isRollingInTheDeep = true;            this.speed = SpeedInit;        }        if (Input.GetKeyDown(KeyCode.Q) && MyGameManager.instance.ActivePlayer.power != 0) {
            int powerMove = MyGameManager.instance.ActivePlayer.power;
            MyGameManager.instance.ActivePlayer.power = 0;
            MyGameManager.instance.ActivePlayer.MyScore.text = (0).ToString();
            MyGameManager.instance.SwitchActivePlayer();
            MyGameManager.instance.ActivePlayer.Move(powerMove);
        }    }}